/* eslint-disable */
// <STARTUP SCRIPT> 
if (!pendingMarkers) {
  // runs only once
  markers = []
  pendingMarkers = []
  lastIndex = null
  freeSlots = []
  usedSlots = []
  drawQueue = []
  boundaries = {}
  intervals = {}
  groups = []
  liquidityAtPrices = {}
  resistances = []
  supports = []
  pendingRedraws = []
  leverages = options.leverages.toString().split(',').sort((a, b) => b - a)
  buyColor = options.buyColor.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1,5).map(a => +a)
  sellColor = options.sellColor.match(/\(([\d.]+),([\d.]+),([\d.]+),?([\d.]+)?\)$/).slice(1,5).map(a => +a)
  buyOpacity = buyColor.pop()
  sellOpacity = sellColor.pop()
  for (var i = 1; i < series.length; i++) {
    if (series[i].seriesType() !== 'BrokenArea') {
      continue
    }

    if (i > 0) {
      freeSlots.push({
        index: i,
        redrawAt: 0
      })
    }

    series[i].setExtensionsBoundaries(boundaries)
  }
}
// </STARTUP SCRIPT> 

// <MARKERS.UTILS> 
if (pendingMarkers.length) {
  markers = markers.concat(pendingMarkers)
  if (series[0].setMarkers) {
    series[0].setMarkers(markers)
  }
  pendingMarkers = []
}
// </MARKERS.UTILS>

// process only on new candle
if (bar.length === lastIndex) {
  return
}

price = avg_heikinashi(bar)
close = price.close
line(price.close, color="transparent") // ghost price for markers

var ohlc4 = (price.open + price.high + price.low + price.close) / 4
var buyVolume = vbuy
var sellVolume = vsell
var avgBuyVolume = sma(buyVolume, options.length)
var avgSellVolume = sma(sellVolume, options.length)
var buyStrength = (options.log ? Math.log(1 + buyVolume / avgBuyVolume * options.logScale) : buyVolume / avgBuyVolume)
var sellStrength = (options.log ? Math.log(1 + sellVolume / avgSellVolume * options.logScale) : sellVolume / avgSellVolume)

var src = price.high
for (let i = 0; i < leverages.length; i++) {
  var levMedian = Math.ceil((src + src * -(100 / (leverages)[i] / 100)) / options.step) * options.step
  
  if (!liquidityAtPrices[levMedian]) {
    var top = levMedian + options.step / 2
    var bottom = levMedian - options.step / 2
    liquidityAtPrices[levMedian] = {
      strength: 0,
      count: 0,
      top: top,
      bottom: bottom
    }
    supports.push(levMedian)
    supports.sort((a, b) => b - a)
  }
  
  liquidityAtPrices[levMedian].count++
  liquidityAtPrices[levMedian].strength = liquidityAtPrices[levMedian].strength + buyStrength
  const alpha = Math.max(options.minAlpha, Math.min(1, liquidityAtPrices[levMedian].strength * liquidityAtPrices[levMedian].count * options.opacity * (buyOpacity || 1)))
  liquidityAtPrices[levMedian].color = 'rgba(' + buyColor.join(',') + ',' + alpha + ')'

  if (liquidityAtPrices[levMedian].strength > options.threshold) {
    
    if (pendingRedraws.indexOf(levMedian) === -1) {
      pendingRedraws.push(levMedian)
    }
  }
}

var src = price.low
for (let i = 0; i < leverages.length; i++) {
  var levMedian = Math.ceil((src + src * (100 / (leverages)[i] / 100)) / options.step) * options.step
  
  if (!liquidityAtPrices[levMedian]) {
    var top = levMedian + options.step / 2
    var bottom = levMedian - options.step / 2
    liquidityAtPrices[levMedian] = {
      strength: 0,
      count: 0,
      top: top,
      bottom: bottom
    }
    resistances.push(levMedian)
    resistances.sort((a, b) => a - b)
  }

  liquidityAtPrices[levMedian].count++
  liquidityAtPrices[levMedian].strength = liquidityAtPrices[levMedian].strength + buyStrength
  const alpha = Math.max(options.minAlpha, Math.min(1, liquidityAtPrices[levMedian].strength * liquidityAtPrices[levMedian].count * options.opacity * (sellOpacity || 1)))
  liquidityAtPrices[levMedian].color = 'rgba(' + sellColor.join(',') + ',' + alpha + ')'
  if (liquidityAtPrices[levMedian].strength > options.threshold) {
    if (pendingRedraws.indexOf(levMedian) === -1) {
      pendingRedraws.push(levMedian)
    }
  }
}

if (pendingRedraws.length) {
  
  var releaseImmediately = []
  for (var i = 0; i < pendingRedraws.length; i++) {
    var cell = liquidityAtPrices[pendingRedraws[i]]
    var slot = freeSlots.find(slot => slot.redrawAt <= bar.length)
    if (slot) {
      var indexFree = freeSlots[freeSlots.indexOf(slot)].index
      var usedIndex = freeSlots.splice(freeSlots.indexOf(slot), 1)[0].index
      usedSlots.push(usedIndex)

      if (cell.id) {
        boundaries[cell.id] = bar.length - 1
      }

      cell.id = Math.random().toString()
      renderer.indicators[indicatorId].series[usedIndex] ={
        id: cell.id,
        time: time,
        lowerValue: cell.top,
        higherValue: cell.bottom,
        extendRight: true,
        color: cell.color
      }

      pendingRedraws.splice(i--, 1)
      releaseImmediately.push(usedIndex)
    }
  }
  for (let i = 0; i < releaseImmediately.length; i++) {
    var plotIndexToFree = usedSlots.splice(usedSlots.indexOf(releaseImmediately[i]), 1)[0]
    
    freeSlots.push({
      index: plotIndexToFree,
      redrawAt: bar.length
    })
  }
}

for (let i = 0; i < supports.length; i++) {
  if (
   price.low <= supports[i]
  ) {
    boundaries[liquidityAtPrices[supports[i]].id] = bar.length
    delete liquidityAtPrices[supports[i]]
    supports.splice(i--, 1)
  } else {
    break;
  }
}

for (let i = 0; i < resistances.length; i++) {
  if (
   price.high >= resistances[i]
  ) {
    boundaries[liquidityAtPrices[resistances[i]].id] = bar.length
    delete liquidityAtPrices[resistances[i]]
    resistances.splice(i--, 1)
  } else {
    break;
  }
}

// set reference to bar index : avoid process next tick
lastIndex = bar.length

